public class Charac {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Integer.toString(456):" + Integer.toString(456));
		System.out.println("Integer.toBinaryString(456):" + Integer.toBinaryString(456));
		System.out.println("Integer.toHexString(456):" + Integer.toHexString(456));
		System.out.println("Integer.toOctalString(456):" + Integer.toOctalString(456));
	}

}

